template.value:
// css:
//     #updates-table {
//     border-bottom: 1px solid #000;
// }

// html:
// <table id="updates-table" class="table" style="width:50%; text-align:center">
//     <thead>
//     <th>Date</th>
//     <th>Command</th>
//     <th>Description</th>
//     </thead>
//     <tbody>
//     <!-- to be filled by javaScript -->
// </tbody>
// </table>

<script type="text/javascript">
const updatesData = document.querySelector("#updates-table > tbody");

function loadUpdates() {
    const request = new XMLHttpRequest();
    request.open("get", "data/dataContainer.json", false);
    request.onload = () => {
        try {
            const json = JSON.parse(request.responseText);
            populateUpdates(json);
        } catch (e) {
            console.warn("Could not load data!")
        }
    }
    request.send();
}

function populateUpdates(json) {
    // Clears out existing table data
    while (updatesData.firstChild) {
        updatesData.removeChild(updatesData.firstChild);
    }
    // Populate table
    json.forEach((row) => {
        const tr = document.createElement("tr");
        row.forEach((cell) => {
            const td = document.createElement("td");
            td.textContent = cell;
            tr.appendChild(td);
        });
        updatesData.appendChild(tr);
    });
}

document.addEventListener("DOMContentLoaded", () => {
    loadUpdates();
});
</script>
